INSERT INTO "public"."users" ("user_id", "type", "first_name", "last_name", "description", "birth_date", "death_date", "nationality", "image", "created_at", "updated_at") VALUES ('1', 'historical_figure', 'Абай', 'Кунанбаев', 'Казахский поэт, философ, музыкант, народный просветитель, общественный деятель, основоположник казахской письменной литературы и её первый классик, реформатор культуры в духе сближения с европейской культурой на основе культуры просвещённого ислама.', '1845-08-10', '1904-07-06', 'Казак', 'https://i.imgur.com/4yTkFku.jpeg', '2024-08-26 13:16:44.157015', '2024-08-26 15:18:08.705087'), ('2', 'historical_figure', 'Мухтар', 'Ауезов', 'Советский казахский писатель, драматург, учёный, общественный деятель, автор романа-эпопеи «Путь Абая», лауреат Ленинской и Государственной премий СССР.', '1897-09-28', '1961-06-27', 'Казак', 'https://i.imgur.com/Mn2s4H2.png', '2024-08-26 13:16:44.157015', '2024-08-26 15:21:37.140025'), ('3', 'historical_figure', 'Шакарим', 'Кудайбердинов', 'Казахский поэт, писатель, философ, переводчик, композитор. Был близким последователем Абая Кунанбаева и продолжателем его литературного и философского наследия.', '1858-07-11', '1931-10-02', 'Казак', 'https://i.imgur.com/8AUvWIr.jpeg', '2024-08-26 13:16:44.157015', '2024-08-26 15:21:06.007938'), ('4', 'historical_figure', 'Ибрай', 'Алтынсари́н', 'Казахский просветитель, педагог, писатель, этнограф. Создал первую казахскую азбуку и открыл множество школ для казахских детей.', '1841-10-20', '1889-07-29', 'Казак', 'https://i.imgur.com/stZzfik.jpeg', '2024-08-26 13:16:44.157015', '2024-08-26 15:21:18.248847'), ('5', 'historical_figure', 'Султанмахмуд', 'Торайгыров', 'Казахский поэт, писатель, публицист. Известен своими произведениями, призывающими к просвещению и развитию национального самосознания.', '1893-11-28', '1920-05-21', 'Казак', 'https://i.imgur.com/POLqhJF.jpeg', '2024-08-26 13:16:44.157015', '2024-08-26 15:20:06.17383'), ('6', 'historical_figure', 'Жамбыл', 'Жабаев', 'Казахский акын, поэт-импровизатор. Один из наиболее известных казахских народных поэтов XX века, автор многочисленных стихов и песен.', '1846-02-28', '1945-06-22', 'Казак', 'https://i.imgur.com/G7tdoeV.jpeg', '2024-08-26 13:16:44.157015', '2024-08-26 15:19:51.4747'), ('7', 'historical_figure', 'Ахмет', 'Байтурсынов', 'Казахский просветитель, лингвист, учёный, педагог, общественный деятель. Известен как реформатор казахского алфавита, создавший казахскую графику на основе арабского письма.', '1872-01-05', '1937-12-08', 'Казак', 'https://i.imgur.com/54kUjb1.jpeg', '2024-08-26 13:16:44.157015', '2024-08-26 15:19:33.649877'), ('8', 'historical_figure', 'Мағжан', 'Жумабаев', 'Казахский поэт, писатель, общественный деятель. Является одной из центральных фигур казахской литературы начала XX века.', '1893-06-25', '1938-03-19', 'Казак', 'https://i.imgur.com/hIszO11.jpeg', '2024-08-26 13:16:44.157015', '2024-08-26 15:19:02.446406'), ('9', 'historical_figure', 'Сакен', 'Сейфуллин', 'Казахский поэт, писатель, общественный деятель. Является одним из основоположников новой казахской литературы.', '1894-10-15', '1938-04-25', 'Казак', 'https://i.imgur.com/OQB4CGJ.jpeg', '2024-08-26 13:16:44.157015', '2024-08-26 15:18:44.276081'), ('10', 'historical_figure', 'Мустафа', 'Шокаев', 'Казахский политик, просветитель, публицист, основатель и лидер партии "Алаш". Один из идеологов казахского национального движения.', '1890-07-07', '1941-12-27', 'Казак', 'https://i.imgur.com/gCrvp34.jpeg', '2024-08-26 13:16:44.157015', '2024-08-26 15:24:35.229553'), ('11', 'historical_figure', 'Сатпаев', 'Каныш', 'Казахский учёный, геолог, первый президент Академии наук Казахской ССР, общественный деятель.', '1899-04-12', '1964-01-31', 'Казак', 'https://i.imgur.com/rYJqI6Y.jpeg', '2024-08-26 13:16:44.157015', '2024-08-26 15:27:38.689004'), ('12', 'historical_figure', 'Джансугуров', 'Ильяс', 'Казахский поэт, писатель, публицист, общественный деятель. Известен своими произведениями, посвящёнными казахской природе 